import { globalInfo } from "../global_Info";

//CREATE ELEMENT FUNCTION
var popUpContainer = '';
var popUpText = '';
var popUpTextWrap = '';
var exitButton = ''
var UIContainer = '';

var popupElementsObject = {
    firstCallHasBeenMade: false,
}

function popupElements(){
    popupElementsObject = {
        popUpContainer: document.createElement('div'),
        popUpText: document.createElement('p'),
        popUpTextWrap: document.createElement('div'),
        exitButton: document.createElement('span'),
        UIContainer: document.querySelectorAll('.ui.container')[6], 
    }
    return popupElementsObject;
}

function createAndAppendPopup(pushText){

    //debugger;
    //create HTML elements (div wrapper and p text)
    // UIContainer = document.querySelectorAll('.ui.container')[6]
    // popUpContainer = document.createElement('div');
    popupElements().popUpContainer.classList.add('popup-container');
    // popUpTextWrap = document.createElement('div');
    popupElements().popUpTextWrap.classList.add('popup-text-wrap');
    // popUpText = document.createElement('p');
    popupElements().popUpText.classList.add('popup-text');
    popupElements().popUpText.innerHTML = 'No message yet recieved';
    popupElements().popUpText.setAttribute("contenteditable","true");
    // exitButton = document.createElement('span');
    popupElements().exitButton.classList.add('popup-exit-button');
    popupElements().exitButton.innerHTML = 'X'
    

    //set initial styles
    popupElements().popUpContainer.style = 
    'display: block; visibility: hidden; width: 320px; height: 170px; background: #884ED5; color: black; z-index: 100; font-family: inherit; justify-content: flex-start; overflow: auto; flex-direction: column; border-radius: 8px;';
    popupElements().popUpTextWrap.style =
    'display: flex; justify-content: center; align-items: center; flex-grow: 1';
    popupElements().popUpText.style = 
    'font-size: 20px; text-align: center; padding: 0px 15px; margin-top: 0px;';
    popupElements().exitButton.style = 
    'margin-right: 10px; margin-top: 5px; font-size: 25px; font-weight: bold; cursor: pointer; text-align: right';

    //append pop up container
    popupElements().UIContainer.appendChild(popUpContainer);

    //append popup text and exit button
    popupElements().UIContainer.appendChild(popupElements().popUpContainer);
    [popupElements().exitButton,popupElements().popUpTextWrap].forEach( element => {
        popupElements().popUpContainer.appendChild(element);
    });
    popupElements().popUpTextWrap.appendChild(popUpText);
}

// globalInfo()
//INSERT PUSH FUNCTION
function insertPush(pushText){
    globalInfo().p_pushText.innerHTML = pushText;
    //when push arrives, display push and insert push text
    globalInfo().popUpContainer.style.display = 'flex';
    globalInfo().popUpContainer.style.visibility = 'visible';
    globalInfo().popUpText.innerHTML = pushText;
    //pushText.innerHTML = pushtext
}






export {popupElements,createAndAppendPopup,insertPush}
