import React, {Suspense, useEffect} from 'react';
import {BlockHeader} from '../globalComponents/blockHeader.jsx';
import {globalInfo} from '../global_Info.js';
import {APILogBuffer,triggerFormPushDataSearch,checkForFormPushData} from '../global_functions.js';
import './apiStatus.css';
import '../index.css';


function Apistatus() {

    var state = {
        title: 'API Testing'
    }

    // START SEARCH - checks API constantly for submissions (push notifcations)
    function startSearchingForPushData(){
        debugger;
        console.log('startSearchingForPushData()')
        console.log(globalInfo)
        triggerFormPushDataSearch();
        console.log('search for notifcations commenced');
        APILogBuffer('search for notifcations commenced');
    }

    // STOP SEARCH - stops searching the API 
    //(effectively halting push notifications, for control panel only NOT users)
    function stopSearchingForPushData(){
        clearInterval(checkForFormPushData);
        console.log('search for notifcations ceased.');
        APILogBuffer('search for notifcations ceased.');
    }

    useEffect(() => { 

    })
  
    return (
    <>
        <div className="api-status ui outer container p-20">

            <BlockHeader title={state.title}/>

            <p>
                <span className='push-text'><b>Last push notification:</b></span>
                <pre className='pre-inline' style={{overflow: 'hidden'}}>No message yet recieved</pre>
            </p>

            <p>
                <span className='api-status'><b>API Status:</b><pre class='pre-inline'>API Has not been activated yet.</pre></span>
            </p>

            <div class="api-status btn-wrap">
                <button class="api-status btn stop" id="force-stop"
                onClick={() => stopSearchingForPushData()}>
                Stop searching
                </button>
                <button class="api-status btn btn-secondary start" id="search-data"
                onClick={() => startSearchingForPushData()}>
                Start searching
                </button>
            </div>

        </div>
    </>
    );
  }

  export {Apistatus}
