import { globalInfo } from "../global_Info";

//CREATE ELEMENT FUNCTION
var popUpContainer = '';
var popUpText = '';
var popUpTextWrap = '';
var exitButton = ''
var UIContainer = '';

var popupElementsObject = {
    firstCallHasBeenMade: false,
}

function popupElements(){
    popupElementsObject = {
        popUpContainer: '',
        popUpText: '',
        popUpTextWrap: '',
        exitButton: '',
        UIContainer: '', 
    }
    return popupElementsObject;
}

function createAndAppendPopup(pushText){

    //debugger;
    //create HTML elements (div wrapper and p text)
    UIContainer = document.querySelectorAll('.ui.container')[6]
    popUpContainer = document.createElement('div');
    popUpContainer.classList.add('popup-container');
    popUpTextWrap = document.createElement('div');
    popUpTextWrap.classList.add('popup-text-wrap');
    popUpText = document.createElement('p');
    popUpText.classList.add('popup-text');
    popUpText.innerHTML = 'No message yet recieved';
    popUpText.setAttribute("contenteditable","true");
    exitButton = document.createElement('span');
    exitButton.classList.add('popup-exit-button');
    exitButton.innerHTML = 'X'
    

    //set initial styles
    popUpContainer.style = 
    'display: block; visibility: hidden; width: 320px; height: 170px; background: #884ED5; color: black; z-index: 100; font-family: inherit; justify-content: flex-start; overflow: auto; flex-direction: column; border-radius: 8px;';
    popUpTextWrap.style =
    'display: flex; justify-content: center; align-items: center; flex-grow: 1'
    popUpText.style = 
    'font-size: 20px; text-align: center; padding: 0px 15px; margin-top: 0px;';
    exitButton.style = 
    'margin-right: 10px; margin-top: 5px; font-size: 25px; font-weight: bold; cursor: pointer; text-align: right';

    //append pop up container
    UIContainer.appendChild(popUpContainer);

    //append popup text and exit button
    UIContainer.appendChild(popUpContainer);
    [exitButton,popUpTextWrap].forEach( element => {
        popUpContainer.appendChild(element);
    });
    popUpTextWrap.appendChild(popUpText);
}

globalInfo()
//INSERT PUSH FUNCTION
function insertPush(pushText){
    globalInfo().p_pushText.innerHTML = pushText;
    //when push arrives, display push and insert push text
    popUpContainer.style.display = 'flex';
    popUpContainer.style.visibility = 'visible';
    popUpText.innerHTML = pushText;
    //pushText.innerHTML = pushtext
}






export {createAndAppendPopup}
